*{
    margin: 0px;
    padding: 0px;
    box-sizing: border-box;
}

.nav-list{
    height: 10vh;
    display: flex; //Coloca los items de manera horizontal
    width: 50%;
    justify-content: space-around; // ajusta el contenido con espacio al rededor de cada item
    align-items: center;
    margin-left: auto;
    list-style: none; //Elimina los estilos por defecto de la lista HTML
}

.nav-links{
    text-decoration: none;
    font-size: 20px;
    color: black;
}

.home, .about, .contact{   
    position: absolute;
    height: 90vh;
    width: 100%;
}

.home-content, .about-content, .contact-content{
    height: 100%;
    display: flex;
    justify-content: space-evenly; //Se ve más coqueto que el around.
    align-items: center; //Alineación central de arriba hacia abajo,
    img{
        height: 70%;
    }
    h1{
        font-size: 60px;
        margin: 30px 0px; //top(arriba) y bottom (abajo) tiene 30px y 0px respectivamente.
    }
    p{
        font-size: 20px;
    }
}

.home{
    background-image: linear-gradient(to top, #d299c2 0%, #fef9d7 100%);
}

.about{
    background-image: linear-gradient(to top, #accbee 0%, #e7f0fd 100%);
}

.contact{
    background-image: linear-gradient(-225deg, #FFFEFF 0%, #D7FFFE 100%);
}
